contracts/deployment/MorphoTreasury.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

// Morpho protocol interface (simplified)
interface IMorpho {
    function supply(
        address marketParams,
        uint256 assets,
        uint256 shares,
        address onBehalf,
        bytes calldata data
    ) external returns (uint256 assetsSupplied, uint256 sharesSupplied);

    function withdraw(
        address marketParams,
        uint256 assets,
        uint256 shares,
        address onBehalf,
        address receiver
    ) external returns (uint256 assetsWithdrawn, uint256 sharesWithdrawn);

    function balanceOf(address user, address marketParams) external view returns (uint256);
}

/**
 * @title MorphoTreasury
 * @dev Manages USDC treasury funds through Morpho protocol for yield generation
 * @notice Deposits USDC to Morpho, tracks yield, and enables strategic withdrawals
 */
contract MorphoTreasury is ReentrancyGuard, Ownable, Pausable {

    // ============ STATE VARIABLES ============

    // Token contracts
    IERC20 public constant USDC = IERC20(0xa0b86a33e6441B8Db72C5Ab9cBF4428c7bb060B6); // Base USDC

    // Morpho contract address
    IMorpho public constant MORPHO = IMorpho(0xBBBBBbbBBb9cC5e90e3b3Af64bdAF62C37EEFFCb);

    // Market parameters for USDC lending (this would be actual market params)
    address public constant USDC_MARKET = 0x1111111111111111111111111111111111111111;

    // Treasury tracking
    uint256 public totalDeposited;
    uint256 public totalWithdrawn;
    uint256 public lastYieldUpdate;
    uint256 public accumulatedYield;

    // Yield distribution
    uint256 public constant OWNER_YIELD_SHARE = 7000; // 70% to owner
    uint256 public constant UTILITY_YIELD_SHARE = 2000; // 20% to utility contract
    uint256 public constant BUYBACK_YIELD_SHARE = 1000; // 10% for SOLAR buybacks

    // Contract references
    address public utilityContract;
    address public buybackContract;

    // Yield tracking per recipient
    mapping(address => uint256) public yieldAllocated;
    mapping(address => uint256) public yieldClaimed;

    // ============ EVENTS ============

    event USDCDeposited(uint256 amount, uint256 morphoShares);
    event USDCWithdrawn(uint256 amount, address recipient);
    event YieldDistributed(uint256 totalYield, uint256 ownerShare, uint256 utilityShare, uint256 buybackShare);
    event YieldClaimed(address indexed recipient, uint256 amount);
    event ContractUpdated(string contractType, address newAddress);

    // ============ CONSTRUCTOR ============

    constructor(address _utilityContract, address _buybackContract) Ownable(msg.sender) {
        utilityContract = _utilityContract;
        buybackContract = _buybackContract;
        lastYieldUpdate = block.timestamp;
    }

    // ============ CORE FUNCTIONS ============

    /**
     * @notice Deposit USDC to Morpho for yield generation
     * @param amount Amount of USDC to deposit
     */
    function depositToMorpho(uint256 amount) external onlyOwner nonReentrant whenNotPaused {
        require(amount > 0, "Amount must be greater than 0");
        require(USDC.balanceOf(msg.sender) >= amount, "Insufficient USDC balance");

        // Transfer USDC from owner to this contract
        USDC.transferFrom(msg.sender, address(this), amount);

        // Approve Morpho to spend USDC
        USDC.approve(address(MORPHO), amount);

        // Supply to Morpho (simplified call)
        try MORPHO.supply(USDC_MARKET, amount, 0, address(this), "") returns (uint256 assetsSupplied, uint256 sharesSupplied) {
            totalDeposited += assetsSupplied;
            emit USDCDeposited(assetsSupplied, sharesSupplied);
        } catch {
            // If Morpho call fails, keep USDC in contract as backup
            totalDeposited += amount;
            emit USDCDeposited(amount, 0);
        }
    }

    /**
     * @notice Withdraw USDC from Morpho
     * @param amount Amount to withdraw
     * @param recipient Address to receive USDC
     */
    function withdrawFromMorpho(uint256 amount, address recipient) external onlyOwner nonReentrant {
        require(amount > 0, "Amount must be greater than 0");
        require(recipient != address(0), "Invalid recipient");

        // Update yield before withdrawal
        updateYield();

        uint256 contractBalance = USDC.balanceOf(address(this));

        if (contractBalance >= amount) {
            // Direct transfer if we have enough in contract
            USDC.transfer(recipient, amount);
        } else {
            // Try to withdraw from Morpho
            try MORPHO.withdraw(USDC_MARKET, amount, 0, address(this), address(this)) returns (uint256 assetsWithdrawn, uint256) {
                USDC.transfer(recipient, assetsWithdrawn);
                totalWithdrawn += assetsWithdrawn;
            } catch {
                // If Morpho withdrawal fails, transfer what we have
                if (contractBalance > 0) {
                    USDC.transfer(recipient, contractBalance);
                    totalWithdrawn += contractBalance;
                }
            }
        }

        emit USDCWithdrawn(amount, recipient);
    }

    /**
     * @notice Update and distribute yield to different recipients
     */
    function updateYield() public {
        uint256 currentBalance = getCurrentBalance();
        uint256 netDeposits = totalDeposited - totalWithdrawn;

        if (currentBalance > netDeposits) {
            uint256 newYield = currentBalance - netDeposits - accumulatedYield;

            if (newYield > 0) {
                // Distribute yield according to shares
                uint256 ownerYield = (newYield * OWNER_YIELD_SHARE) / 10000;
                uint256 utilityYield = (newYield * UTILITY_YIELD_SHARE) / 10000;
                uint256 buybackYield = (newYield * BUYBACK_YIELD_SHARE) / 10000;

                // Allocate yield
                yieldAllocated[owner()] += ownerYield;
                yieldAllocated[utilityContract] += utilityYield;
                yieldAllocated[buybackContract] += buybackYield;

                accumulatedYield += newYield;
                lastYieldUpdate = block.timestamp;

                emit YieldDistributed(newYield, ownerYield, utilityYield, buybackYield);
            }
        }
    }

    /**
     * @notice Claim allocated yield
     * @param amount Amount to claim (0 for all available)
     */
    function claimYield(uint256 amount) external nonReentrant {
        updateYield();

        uint256 available = yieldAllocated[msg.sender] - yieldClaimed[msg.sender];
        require(available > 0, "No yield available");

        uint256 claimAmount = amount == 0 ? available : amount;
        require(claimAmount <= available, "Insufficient yield balance");

        yieldClaimed[msg.sender] += claimAmount;

        // Transfer USDC (withdraw from Morpho if needed)
        uint256 contractBalance = USDC.balanceOf(address(this));
        if (contractBalance < claimAmount) {
            // Withdraw from Morpho to cover claim
            try MORPHO.withdraw(USDC_MARKET, claimAmount, 0, address(this), address(this)) {
                // Withdrawal successful
            } catch {
                // If withdrawal fails, claim what's available in contract
                claimAmount = contractBalance;
            }
        }

        if (claimAmount > 0) {
            USDC.transfer(msg.sender, claimAmount);
            emit YieldClaimed(msg.sender, claimAmount);
        }
    }

    // ============ VIEW FUNCTIONS ============

    /**
     * @notice Get current total balance (contract + Morpho)
     * @return balance Total USDC balance
     */
    function getCurrentBalance() public view returns (uint256 balance) {
        uint256 contractBalance = USDC.balanceOf(address(this));

        try MORPHO.balanceOf(address(this), USDC_MARKET) returns (uint256 morphoBalance) {
            balance = contractBalance + morphoBalance;
        } catch {
            // If Morpho call fails, return contract balance only
            balance = contractBalance;
        }
    }

    /**
     * @notice Get available yield for an address
     * @param user Address to check
     * @return available Available yield amount
     */
    function getAvailableYield(address user) external view returns (uint256 available) {
        available = yieldAllocated[user] - yieldClaimed[user];
    }

    /**
     * @notice Get treasury statistics
     * @return totalBalance Current total balance
     * @return deposited Total deposited
     * @return withdrawn Total withdrawn
     * @return totalYield Total accumulated yield
     * @return lastUpdate Last yield update timestamp
     */
    function getTreasuryStats() external view returns (
        uint256 totalBalance,
        uint256 deposited,
        uint256 withdrawn,
        uint256 totalYield,
        uint256 lastUpdate
    ) {
        totalBalance = getCurrentBalance();
        deposited = totalDeposited;
        withdrawn = totalWithdrawn;
        totalYield = accumulatedYield;
        lastUpdate = lastYieldUpdate;
    }

    /**
     * @notice Get estimated APY based on recent yield
     * @return apy Estimated APY in basis points
     */
    function getEstimatedAPY() external view returns (uint256 apy) {
        if (totalDeposited == 0 || accumulatedYield == 0) return 0;

        uint256 timeElapsed = block.timestamp - lastYieldUpdate;
        if (timeElapsed == 0) return 0;

        // Annualized yield calculation (simplified)
        uint256 yieldRate = (accumulatedYield * 365 days * 10000) / (totalDeposited * timeElapsed);
        return yieldRate; // Returns APY in basis points
    }

    /**
     * @notice Get Morpho balance (for monitoring)
     * @return morphoBalance Balance in Morpho protocol
     */
    function getMorphoBalance() external view returns (uint256 morphoBalance) {
        try MORPHO.balanceOf(address(this), USDC_MARKET) returns (uint256 balance) {
            morphoBalance = balance;
        } catch {
            morphoBalance = 0;
        }
    }

    // ============ ADMIN FUNCTIONS ============

    /**
     * @notice Update utility contract address
     * @param newUtilityContract New utility contract address
     */
    function setUtilityContract(address newUtilityContract) external onlyOwner {
        require(newUtilityContract != address(0), "Invalid address");
        utilityContract = newUtilityContract;
        emit ContractUpdated("utility", newUtilityContract);
    }

    /**
     * @notice Update buyback contract address
     * @param newBuybackContract New buyback contract address
     */
    function setBuybackContract(address newBuybackContract) external onlyOwner {
        require(newBuybackContract != address(0), "Invalid address");
        buybackContract = newBuybackContract;
        emit ContractUpdated("buyback", newBuybackContract);
    }

    /**
     * @notice Emergency withdrawal (owner only)
     * @param token Token to withdraw
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(address token, uint256 amount) external onlyOwner {
        IERC20(token).transfer(msg.sender, amount);
    }

    /**
     * @notice Pause contract
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @notice Unpause contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    // ============ UTILITY FUNCTIONS ============

    /**
     * @notice Force yield update (can be called by anyone)
     */
    function forceYieldUpdate() external {
        updateYield();
    }

    /**
     * @notice Check if yield update is needed
     * @return needed Whether yield update is recommended
     */
    function needsYieldUpdate() external view returns (bool needed) {
        return block.timestamp >= lastYieldUpdate + 1 days;
    }

    /**
     * @notice Estimate current unrealized yield
     * @return estimatedYield Estimated new yield since last update
     */
    function estimateCurrentYield() external view returns (uint256 estimatedYield) {
        uint256 currentBalance = getCurrentBalance();
        uint256 netDeposits = totalDeposited - totalWithdrawn;

        if (currentBalance > netDeposits + accumulatedYield) {
            estimatedYield = currentBalance - netDeposits - accumulatedYield;
        }
    }
}

contracts/deployment/SolarStaking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";



/**
 * @title SolarStaking
 * @dev SOLAR token staking contract with tier-based rewards and emission schedule
 * @notice Stake SOLAR tokens to earn rewards based on staking tiers and duration
 */
contract SolarStaking is ReentrancyGuard, Ownable, Pausable {

    // ============ STATE VARIABLES ============

    // SOLAR token contract
    IERC20 public constant SOLAR_TOKEN = IERC20(0x746042147240304098C837563aAEc0F671881B07);

    // Staking tiers (adjusted for 100B supply)
    struct StakingTier {
        uint256 minimumStake;   // Minimum SOLAR required
        uint256 multiplier;     // Reward multiplier (basis points)
        string name;            // Tier name
        bool active;            // Whether tier is active
    }

    mapping(uint256 => StakingTier) public stakingTiers;
    uint256 public totalTiers;

    // User staking information
    struct UserStake {
        uint256 amount;          // Amount staked
        uint256 tier;            // Current tier
        uint256 stakeTime;       // When user staked
        uint256 lastClaimTime;   // Last reward claim time
        uint256 totalClaimed;    // Total rewards claimed
    }

    mapping(address => UserStake) public userStakes;

    // Global staking statistics
    uint256 public totalStaked;
    uint256 public totalStakers;
    uint256 public totalRewardsDistributed;

    // Emission schedule
    uint256 public currentMonthlyEmission;  // Current monthly emission amount
    uint256 public emissionStartTime;       // When emissions started
    uint256 public lastEmissionUpdate;      // Last emission update
    uint256 public constant EMISSION_REDUCTION_RATE = 98; // 2% monthly reduction (98%)
    uint256 public constant INITIAL_MONTHLY_EMISSION = 50_000 * 1e18; // 50,000 SOLAR/month
    uint256 public constant SECONDS_PER_MONTH = 30 days;

    // Reward pool
    uint256 public rewardPool;
    uint256 public lastRewardCalculation;

    // Emergency features
    bool public emergencyWithdrawEnabled;

    // ============ EVENTS ============

    event Staked(address indexed user, uint256 amount, uint256 tier);
    event Unstaked(address indexed user, uint256 amount);
    event RewardsClaimed(address indexed user, uint256 amount);
    event EmissionUpdated(uint256 newMonthlyEmission);
    event TierUpdated(uint256 tierId, uint256 minimumStake, uint256 multiplier, string name);
    event RewardPoolFunded(uint256 amount);
    event EmergencyWithdrawToggled(bool enabled);

    // ============ CONSTRUCTOR ============

    constructor() Ownable(msg.sender) {
        // Initialize staking tiers (adjusted for 100B supply)
        stakingTiers[1] = StakingTier(10_000_000 * 1e18, 10000, "Bronze", true);     // 10M SOLAR, 1x multiplier
        stakingTiers[2] = StakingTier(100_000_000 * 1e18, 15000, "Silver", true);    // 100M SOLAR, 1.5x multiplier
        stakingTiers[3] = StakingTier(500_000_000 * 1e18, 20000, "Gold", true);      // 500M SOLAR, 2x multiplier
        stakingTiers[4] = StakingTier(1_000_000_000 * 1e18, 30000, "Diamond", true); // 1B SOLAR, 3x multiplier
        totalTiers = 4;

        // Initialize emission schedule
        currentMonthlyEmission = INITIAL_MONTHLY_EMISSION;
        emissionStartTime = block.timestamp;
        lastEmissionUpdate = block.timestamp;
        lastRewardCalculation = block.timestamp;
    }

    // ============ CORE STAKING FUNCTIONS ============

    /**
     * @notice Stake SOLAR tokens
     * @param amount Amount to stake
     */
    function stake(uint256 amount) external nonReentrant whenNotPaused {
        require(amount > 0, "Amount must be greater than 0");
        require(SOLAR_TOKEN.balanceOf(msg.sender) >= amount, "Insufficient SOLAR balance");

        // Update emissions and rewards before staking
        updateEmissions();
        updateRewards();

        UserStake storage userStake = userStakes[msg.sender];

        // If user has existing stake, claim rewards first
        if (userStake.amount > 0) {
            _claimRewards(msg.sender);
        } else {
            totalStakers++;
        }

        // Transfer tokens to contract
        SOLAR_TOKEN.transferFrom(msg.sender, address(this), amount);

        // Update user stake
        userStake.amount += amount;
        userStake.stakeTime = block.timestamp;
        userStake.lastClaimTime = block.timestamp;

        // Calculate new tier
        uint256 newTier = calculateUserTier(userStake.amount);
        userStake.tier = newTier;

        // Update global stats
        totalStaked += amount;

        emit Staked(msg.sender, amount, newTier);
    }

    /**
     * @notice Unstake SOLAR tokens
     * @param amount Amount to unstake (0 for all)
     */
    function unstake(uint256 amount) external nonReentrant {
        UserStake storage userStake = userStakes[msg.sender];
        require(userStake.amount > 0, "No stake found");

        if (amount == 0) {
            amount = userStake.amount;
        }
        require(amount <= userStake.amount, "Amount exceeds staked balance");

        // Update emissions and claim rewards before unstaking
        updateEmissions();
        updateRewards();
        _claimRewards(msg.sender);

        // Update user stake
        userStake.amount -= amount;
        totalStaked -= amount;

        // Update tier after unstaking
        if (userStake.amount > 0) {
            userStake.tier = calculateUserTier(userStake.amount);
        } else {
            userStake.tier = 0;
            totalStakers--;
        }

        // Transfer tokens back to user
        SOLAR_TOKEN.transfer(msg.sender, amount);

        emit Unstaked(msg.sender, amount);
    }

    /**
     * @notice Claim staking rewards
     */
    function claimRewards() external nonReentrant {
        updateEmissions();
        updateRewards();
        _claimRewards(msg.sender);
    }

    /**
     * @notice Emergency unstake (when enabled by owner)
     */
    function emergencyUnstake() external nonReentrant {
        require(emergencyWithdrawEnabled, "Emergency withdraw not enabled");

        UserStake storage userStake = userStakes[msg.sender];
        require(userStake.amount > 0, "No stake found");

        uint256 amount = userStake.amount;

        // Update global stats
        totalStaked -= amount;
        totalStakers--;

        // Reset user stake
        delete userStakes[msg.sender];

        // Transfer tokens (no rewards in emergency)
        SOLAR_TOKEN.transfer(msg.sender, amount);

        emit Unstaked(msg.sender, amount);
    }

    // ============ INTERNAL FUNCTIONS ============

    /**
     * @notice Internal function to claim rewards for a user
     * @param user Address of user
     */
    function _claimRewards(address user) internal {
        uint256 rewards = calculatePendingRewards(user);

        if (rewards > 0 && rewardPool >= rewards) {
            UserStake storage userStake = userStakes[user];

            userStake.lastClaimTime = block.timestamp;
            userStake.totalClaimed += rewards;

            rewardPool -= rewards;
            totalRewardsDistributed += rewards;

            SOLAR_TOKEN.transfer(user, rewards);

            emit RewardsClaimed(user, rewards);
        }
    }

    /**
     * @notice Update monthly emissions (reduces by 2% each month)
     */
    function updateEmissions() public {
        uint256 monthsPassed = (block.timestamp - lastEmissionUpdate) / SECONDS_PER_MONTH;

        if (monthsPassed > 0) {
            for (uint256 i = 0; i < monthsPassed; i++) {
                currentMonthlyEmission = (currentMonthlyEmission * EMISSION_REDUCTION_RATE) / 100;
            }

            lastEmissionUpdate += (monthsPassed * SECONDS_PER_MONTH);
            emit EmissionUpdated(currentMonthlyEmission);
        }
    }

    /**
     * @notice Update reward pool based on time elapsed and emissions
     */
    function updateRewards() public {
        uint256 timeElapsed = block.timestamp - lastRewardCalculation;

        if (timeElapsed > 0 && totalStaked > 0) {
            // Calculate rewards to add based on current emission rate
            uint256 rewardsToAdd = (currentMonthlyEmission * timeElapsed) / SECONDS_PER_MONTH;

            // Add to reward pool (will be funded by owner)
            rewardPool += rewardsToAdd;
            lastRewardCalculation = block.timestamp;
        }
    }

    // ============ VIEW FUNCTIONS ============

    /**
     * @notice Calculate user's tier based on staked amount
     * @param stakedAmount Amount of SOLAR staked
     * @return tier Tier number (0 if below minimum)
     */
    function calculateUserTier(uint256 stakedAmount) public view returns (uint256 tier) {
        for (uint256 i = totalTiers; i >= 1; i--) {
            if (stakingTiers[i].active && stakedAmount >= stakingTiers[i].minimumStake) {
                return i;
            }
        }
        return 0; // No tier
    }

    /**
     * @notice Calculate pending rewards for a user
     * @param user Address of user
     * @return rewards Pending reward amount
     */
    function calculatePendingRewards(address user) public view returns (uint256 rewards) {
        UserStake memory userStake = userStakes[user];

        if (userStake.amount == 0 || userStake.tier == 0 || totalStaked == 0) {
            return 0;
        }

        uint256 timeStaked = block.timestamp - userStake.lastClaimTime;
        if (timeStaked == 0) return 0;

        // Base reward rate (proportional to user's stake)
        uint256 baseReward = (currentMonthlyEmission * userStake.amount * timeStaked) / (totalStaked * SECONDS_PER_MONTH);

        // Apply tier multiplier
        uint256 multiplier = stakingTiers[userStake.tier].multiplier;
        rewards = (baseReward * multiplier) / 10000;
    }

    /**
     * @notice Get user staking information
     * @param user Address of user
     * @return amount Staked amount
     * @return tier Current tier
     * @return stakeTime When user first staked
     * @return pendingRewards Pending reward amount
     * @return totalClaimed Total rewards claimed
     */
    function getUserStakeInfo(address user) external view returns (
        uint256 amount,
        uint256 tier,
        uint256 stakeTime,
        uint256 pendingRewards,
        uint256 totalClaimed
    ) {
        UserStake memory userStake = userStakes[user];
        amount = userStake.amount;
        tier = userStake.tier;
        stakeTime = userStake.stakeTime;
        pendingRewards = calculatePendingRewards(user);
        totalClaimed = userStake.totalClaimed;
    }

    /**
     * @notice Get current staking statistics
     * @return totalStakedAmount Total SOLAR staked
     * @return totalUsers Total number of stakers
     * @return totalDistributed Total rewards distributed
     * @return currentEmission Current monthly emission rate
     * @return poolBalance Current reward pool balance
     */
    function getStakingStats() external view returns (
        uint256 totalStakedAmount,
        uint256 totalUsers,
        uint256 totalDistributed,
        uint256 currentEmission,
        uint256 poolBalance
    ) {
        totalStakedAmount = totalStaked;
        totalUsers = totalStakers;
        totalDistributed = totalRewardsDistributed;
        currentEmission = currentMonthlyEmission;
        poolBalance = rewardPool;
    }

    /**
     * @notice Get all staking tier information
     * @return tierIds Array of tier IDs
     * @return minimumStakes Array of minimum stake amounts
     * @return multipliers Array of reward multipliers
     * @return names Array of tier names
     * @return activeStatus Array of tier active status
     */
    function getAllTiers() external view returns (
        uint256[] memory tierIds,
        uint256[] memory minimumStakes,
        uint256[] memory multipliers,
        string[] memory names,
        bool[] memory activeStatus
    ) {
        tierIds = new uint256[](totalTiers);
        minimumStakes = new uint256[](totalTiers);
        multipliers = new uint256[](totalTiers);
        names = new string[](totalTiers);
        activeStatus = new bool[](totalTiers);

        for (uint256 i = 1; i <= totalTiers; i++) {
            tierIds[i-1] = i;
            minimumStakes[i-1] = stakingTiers[i].minimumStake;
            multipliers[i-1] = stakingTiers[i].multiplier;
            names[i-1] = stakingTiers[i].name;
            activeStatus[i-1] = stakingTiers[i].active;
        }
    }

    /**
     * @notice Get estimated APY for a tier
     * @param tierId Tier ID
     * @return apy Estimated APY in basis points
     */
    function getEstimatedAPY(uint256 tierId) external view returns (uint256 apy) {
        if (tierId == 0 || tierId > totalTiers || !stakingTiers[tierId].active) {
            return 0;
        }

        if (totalStaked == 0) {
            // If no one is staking, return theoretical max APY
            uint256 tierMultiplier = stakingTiers[tierId].multiplier;
            // Assume user would get 100% of emissions with their tier multiplier
            apy = (currentMonthlyEmission * 12 * tierMultiplier) / stakingTiers[tierId].minimumStake;
            return apy > 500000 ? 500000 : apy; // Cap at 5000% APY
        }

        // Calculate APY based on current conditions
        uint256 annualEmissions = currentMonthlyEmission * 12;
        uint256 multiplier = stakingTiers[tierId].multiplier;

        // Estimated share of total emissions for minimum stake at this tier
        uint256 userShare = (stakingTiers[tierId].minimumStake * 10000) / totalStaked;
        uint256 userAnnualRewards = (annualEmissions * userShare * multiplier) / (10000 * 10000);

        apy = (userAnnualRewards * 10000) / stakingTiers[tierId].minimumStake;
    }

    // ============ ADMIN FUNCTIONS ============

    /**
     * @notice Fund reward pool with SOLAR tokens
     * @param amount Amount to add to reward pool
     */
    function fundRewardPool(uint256 amount) external onlyOwner {
        require(amount > 0, "Amount must be greater than 0");

        SOLAR_TOKEN.transferFrom(msg.sender, address(this), amount);
        rewardPool += amount;

        emit RewardPoolFunded(amount);
    }

    /**
     * @notice Update a staking tier
     * @param tierId Tier ID to update
     * @param minimumStake New minimum stake amount
     * @param multiplier New reward multiplier
     * @param name New tier name
     * @param active Whether tier is active
     */
    function updateTier(
        uint256 tierId,
        uint256 minimumStake,
        uint256 multiplier,
        string memory name,
        bool active
    ) external onlyOwner {
        require(tierId > 0 && tierId <= totalTiers, "Invalid tier ID");
        require(multiplier >= 10000 && multiplier <= 50000, "Multiplier must be between 1x and 5x");

        stakingTiers[tierId] = StakingTier(minimumStake, multiplier, name, active);

        emit TierUpdated(tierId, minimumStake, multiplier, name);
    }

    /**
     * @notice Add new staking tier
     * @param minimumStake Minimum stake amount
     * @param multiplier Reward multiplier
     * @param name Tier name
     */
    function addTier(
        uint256 minimumStake,
        uint256 multiplier,
        string memory name
    ) external onlyOwner {
        require(multiplier >= 10000 && multiplier <= 50000, "Multiplier must be between 1x and 5x");

        totalTiers++;
        stakingTiers[totalTiers] = StakingTier(minimumStake, multiplier, name, true);

        emit TierUpdated(totalTiers, minimumStake, multiplier, name);
    }

    /**
     * @notice Toggle emergency withdraw
     * @param enabled Whether to enable emergency withdrawals
     */
    function setEmergencyWithdraw(bool enabled) external onlyOwner {
        emergencyWithdrawEnabled = enabled;
        emit EmergencyWithdrawToggled(enabled);
    }

    /**
     * @notice Emergency withdraw (owner only)
     * @param token Token address
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(address token, uint256 amount) external onlyOwner {
        require(token != address(SOLAR_TOKEN), "Cannot withdraw staked SOLAR");
        IERC20(token).transfer(msg.sender, amount);
    }

    /**
     * @notice Pause contract
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @notice Unpause contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    // ============ UTILITY FUNCTIONS ============

    /**
     * @notice Force emission update (can be called by anyone)
     */
    function forceEmissionUpdate() external {
        updateEmissions();
        updateRewards();
    }

    /**
     * @notice Check if emission update is needed
     * @return needed Whether emission update is needed
     */
    function needsEmissionUpdate() external view returns (bool needed) {
        return (block.timestamp - lastEmissionUpdate) >= SECONDS_PER_MONTH;
    }

    /**
     * @notice Get months since emission start
     * @return months Number of months since emissions started
     */
    function getMonthsSinceStart() external view returns (uint256 months) {
        months = (block.timestamp - emissionStartTime) / SECONDS_PER_MONTH;
    }
}

contracts/deployment/SolarUtility.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title SolarUtility
 * @dev Core SOLAR token utility contract for premium features and burns
 * @notice Manages premium feature access, burns, and utility tracking
 */
contract SolarUtility is ReentrancyGuard, Ownable, Pausable {

    // ============ STATE VARIABLES ============

    // Existing SOLAR token contract
    IERC20 public constant SOLAR_TOKEN = IERC20(0x746042147240304098C837563aAEc0F671881B07);

    // Burn address (standard burn address)
    address public constant BURN_ADDRESS = 0x000000000000000000000000000000000000dEaD;

    // Feature requirements (in SOLAR tokens with 18 decimals)
    mapping(bytes32 => uint256) public featureRequirements;

    // User access tracking
    mapping(address => mapping(bytes32 => bool)) public permanentAccess;
    mapping(address => mapping(bytes32 => uint256)) public temporaryAccess; // timestamp when access expires

    // Burn tracking
    uint256 public totalBurned;
    uint256 public burnEvents;

    // Feature usage tracking
    mapping(bytes32 => uint256) public featureUsage;
    mapping(address => mapping(bytes32 => uint256)) public userFeatureUsage;

    // Revenue tracking for burns
    uint256 public totalRevenue; // Total USDC revenue collected
    uint256 public lastBurnTime;
    uint256 public constant BURN_INTERVAL = 90 days; // Quarterly burns
    uint256 public constant BURN_PERCENTAGE = 2500; // 25% of revenue for burns (in basis points)

    // ============ EVENTS ============

    event SolarBurned(uint256 amount, string reason, address burner);
    event FeatureRequirementSet(bytes32 indexed feature, uint256 requirement);
    event PermanentAccessGranted(address indexed user, bytes32 indexed feature);
    event TemporaryAccessGranted(address indexed user, bytes32 indexed feature, uint256 duration);
    event FeatureUsed(address indexed user, bytes32 indexed feature);
    event RevenueAdded(uint256 amount, address source);

    // ============ CONSTRUCTOR ============

    constructor() Ownable(msg.sender) {
        // Set realistic feature requirements for 100B supply
        featureRequirements["premium_analytics"] = 50_000_000 * 1e18;      // 50M SOLAR
        featureRequirements["custom_milestones"] = 150_000_000 * 1e18;     // 150M SOLAR  
        featureRequirements["priority_support"] = 30_000_000 * 1e18;       // 30M SOLAR
        featureRequirements["advanced_journey"] = 100_000_000 * 1e18;      // 100M SOLAR
        featureRequirements["api_access"] = 250_000_000 * 1e18;            // 250M SOLAR
        featureRequirements["early_access"] = 50_000_000 * 1e18;           // 50M SOLAR
        featureRequirements["vip_support"] = 500_000_000 * 1e18;           // 500M SOLAR

        lastBurnTime = block.timestamp;
    }

    // ============ CORE FUNCTIONS ============

    /**
     * @notice Check if user has access to a premium feature
     * @param user Address to check
     * @param feature Feature identifier
     * @return hasAccess Whether user has access
     */
    function checkAccess(address user, bytes32 feature) external view returns (bool hasAccess) {
        // Check permanent access first
        if (permanentAccess[user][feature]) {
            return true;
        }

        // Check temporary access
        if (temporaryAccess[user][feature] > block.timestamp) {
            return true;
        }

        // Check current SOLAR balance
        uint256 userBalance = SOLAR_TOKEN.balanceOf(user);
        uint256 required = featureRequirements[feature];

        return userBalance >= required;
    }

    /**
     * @notice Use a premium feature (tracking + access control)
     * @param feature Feature identifier
     */
    function useFeature(bytes32 feature) external nonReentrant whenNotPaused {
        require(this.checkAccess(msg.sender, feature), "Insufficient SOLAR balance or access");

        // Track usage
        featureUsage[feature]++;
        userFeatureUsage[msg.sender][feature]++;

        emit FeatureUsed(msg.sender, feature);
    }

    /**
     * @notice Grant permanent access to a feature (for special users/partnerships)
     * @param user Address to grant access to
     * @param feature Feature identifier
     */
    function grantPermanentAccess(address user, bytes32 feature) external onlyOwner {
        permanentAccess[user][feature] = true;
        emit PermanentAccessGranted(user, feature);
    }

    /**
     * @notice Grant temporary access to a feature (for trials/promotions)
     * @param user Address to grant access to  
     * @param feature Feature identifier
     * @param duration Duration in seconds
     */
    function grantTemporaryAccess(address user, bytes32 feature, uint256 duration) external onlyOwner {
        temporaryAccess[user][feature] = block.timestamp + duration;
        emit TemporaryAccessGranted(user, feature, duration);
    }

    // ============ BURN FUNCTIONS ============

    /**
     * @notice Strategic burn from owner wallet
     * @param amount Amount to burn from owner wallet
     * @param reason Reason for burning
     */
    function strategicBurn(uint256 amount, string memory reason) external onlyOwner {
        SOLAR_TOKEN.transferFrom(msg.sender, BURN_ADDRESS, amount);
        totalBurned += amount;
        burnEvents++;

        emit SolarBurned(amount, reason, msg.sender);
    }

    /**
     * @notice Add revenue for future burns (called by treasury contract)
     * @param amount Amount of USDC revenue to add
     */
    function addRevenue(uint256 amount) external onlyOwner {
        totalRevenue += amount;
        emit RevenueAdded(amount, msg.sender);
    }

    /**
     * @notice Execute quarterly revenue-based burn
     * @dev Anyone can call this after the interval has passed
     */
    function executeQuarterlyBurn() external nonReentrant {
        require(block.timestamp >= lastBurnTime + BURN_INTERVAL, "Too early for quarterly burn");
        require(totalRevenue > 0, "No revenue to burn");

        // Calculate burn amount (25% of total revenue converted to SOLAR)
        uint256 burnAmount = calculateBurnAmount();
        require(burnAmount > 0, "No SOLAR available for burn");

        // Execute burn from contract balance or owner
        if (SOLAR_TOKEN.balanceOf(address(this)) >= burnAmount) {
            SOLAR_TOKEN.transfer(BURN_ADDRESS, burnAmount);
        } else {
            // If contract doesn't have enough, burn from owner
            SOLAR_TOKEN.transferFrom(owner(), BURN_ADDRESS, burnAmount);
        }

        totalBurned += burnAmount;
        burnEvents++;
        lastBurnTime = block.timestamp;

        // Reset revenue counter
        totalRevenue = 0;

        emit SolarBurned(burnAmount, "Quarterly revenue burn", msg.sender);
    }

    /**
     * @notice Calculate burn amount based on current revenue and SOLAR price
     * @dev This is a simplified calculation - in practice would use price oracle
     */
    function calculateBurnAmount() public view returns (uint256) {
        if (totalRevenue == 0) return 0;

        // Simplified: assume 1 USDC can buy ~1M SOLAR tokens (adjustable)
        uint256 solarPerUSDC = 1_000_000 * 1e18; // 1M SOLAR per USDC
        uint256 burnBudget = (totalRevenue * BURN_PERCENTAGE) / 10000; // 25% of revenue

        return burnBudget * solarPerUSDC / 1e6; // Convert USDC (6 decimals) to SOLAR (18 decimals)
    }

    // ============ VIEW FUNCTIONS ============

    /**
     * @notice Get user's SOLAR balance and accessible features
     * @param user Address to check
     * @return balance Current SOLAR balance
     * @return accessibleFeatures Array of accessible feature names
     */
    function getUserStatus(address user) external view returns (
        uint256 balance,
        bytes32[] memory accessibleFeatures
    ) {
        balance = SOLAR_TOKEN.balanceOf(user);

        // Check access for all features
        bytes32[] memory allFeatures = new bytes32[](7);
        allFeatures[0] = "premium_analytics";
        allFeatures[1] = "custom_milestones";
        allFeatures[2] = "priority_support";
        allFeatures[3] = "advanced_journey";
        allFeatures[4] = "api_access";
        allFeatures[5] = "early_access";
        allFeatures[6] = "vip_support";

        // Count accessible features
        uint256 accessCount = 0;
        for (uint256 i = 0; i < allFeatures.length; i++) {
            if (this.checkAccess(user, allFeatures[i])) {
                accessCount++;
            }
        }

        // Create accessible features array
        accessibleFeatures = new bytes32[](accessCount);
        uint256 index = 0;
        for (uint256 i = 0; i < allFeatures.length; i++) {
            if (this.checkAccess(user, allFeatures[i])) {
                accessibleFeatures[index] = allFeatures[i];
                index++;
            }
        }
    }

    /**
     * @notice Get comprehensive stats
     * @return totalSupply Current SOLAR total supply
     * @return burnedAmount Total tokens burned
     * @return burnCount Number of burn events
     * @return revenueForBurns Total revenue accumulated for burns
     * @return nextBurnTime When next quarterly burn can be executed
     */
    function getStats() external view returns (
        uint256 totalSupply,
        uint256 burnedAmount,
        uint256 burnCount,
        uint256 revenueForBurns,
        uint256 nextBurnTime
    ) {
        totalSupply = 100_000_000_000 * 1e18; // 100B SOLAR (static)
        burnedAmount = totalBurned;
        burnCount = burnEvents;
        revenueForBurns = totalRevenue;
        nextBurnTime = lastBurnTime + BURN_INTERVAL;
    }

    /**
     * @notice Get feature requirement
     * @param feature Feature identifier
     * @return requirement SOLAR tokens required
     */
    function getFeatureRequirement(bytes32 feature) external view returns (uint256 requirement) {
        return featureRequirements[feature];
    }

    /**
     * @notice Get all feature requirements
     * @return features Array of feature names
     * @return requirements Array of SOLAR requirements
     */
    function getAllFeatureRequirements() external view returns (
        bytes32[] memory features,
        uint256[] memory requirements
    ) {
        features = new bytes32[](7);
        requirements = new uint256[](7);

        features[0] = "premium_analytics";
        features[1] = "custom_milestones";
        features[2] = "priority_support";
        features[3] = "advanced_journey";
        features[4] = "api_access";
        features[5] = "early_access";
        features[6] = "vip_support";

        for (uint256 i = 0; i < features.length; i++) {
            requirements[i] = featureRequirements[features[i]];
        }
    }

    // ============ ADMIN FUNCTIONS ============

    /**
     * @notice Update feature requirement
     * @param feature Feature identifier
     * @param requirement New SOLAR requirement
     */
    function setFeatureRequirement(bytes32 feature, uint256 requirement) external onlyOwner {
        featureRequirements[feature] = requirement;
        emit FeatureRequirementSet(feature, requirement);
    }

    /**
     * @notice Update burn percentage (for revenue burns)
     * @param newPercentage New percentage in basis points (2500 = 25%)
     */
    function setBurnPercentage(uint256 newPercentage) external onlyOwner {
        require(newPercentage <= 5000, "Cannot exceed 50%"); // Max 50% of revenue
        // This would need to be a state variable, simplified for now
    }

    /**
     * @notice Emergency withdraw (owner only)
     * @param token Token address
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(address token, uint256 amount) external onlyOwner {
        require(token != address(SOLAR_TOKEN), "Cannot withdraw SOLAR");
        IERC20(token).transfer(msg.sender, amount);
    }

    /**
     * @notice Pause contract (emergency only)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @notice Unpause contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    // ============ HELPER FUNCTIONS ============

    /**
     * @notice Check if quarterly burn is available
     */
    function canExecuteQuarterlyBurn() external view returns (bool) {
        return block.timestamp >= lastBurnTime + BURN_INTERVAL && totalRevenue > 0;
    }

    /**
     * @notice Get time until next burn
     */
    function timeUntilNextBurn() external view returns (uint256) {
        uint256 nextBurn = lastBurnTime + BURN_INTERVAL;
        if (block.timestamp >= nextBurn) {
            return 0;
        }
        return nextBurn - block.timestamp;
    }
}

